//to find a number is odd or even
public class If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=7;
		
		
		if (num % 2==0)
		{
			System.out.println("The number is even");
		}
		
	
		
		System.out.println("End of the program");
			
		
		

	}

}
