
public class pgm2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 4;
		int b = 2;
		
		int add = a+b;
		int sub = a-b;
		int mul = a*b;
		int div = a/b;
		int mod = a%b;
		
		
		System.out.println(add + "\n" + sub + "\n" + mul + "\n" +  div + "\n" + mod + "\n");
		
		
		}

}
