package session1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = -10;
		float b = 10.453f;
		double c = 11.22;
		boolean myBool = true;
		String myText = "Hello!";
		char myLetter = 'D';
		
		System.out.println("Whole number is :" + a + "\n");
		System.out.println("Floating number is:" + b + "\n");
		System.out.println("The number double is:" + c + "\n");
		System.out.println("Boolean number is:" + myBool + "\n");
		System.out.println("The String is:" + myText+ "\n");
		System.out.println("The character is :" + myLetter + "\n") ;
		
		
	}

}
